ick(){return _this3.handleGenreClick(3,35);},className:this.state.genreClasses[3],children:[/*#__PURE__*/_jsx("img",{className:"genreImage",src:ComedySVG,alt:""}),/*#__PURE__*/_jsx("p",{className:"genreName",children:"Comedy"})]}),/*#__PURE__*/_jsxs("div",{onClick:function onClick(){return _this3.handleGenreClick(4,27);},className:this.state.genreClasses[4],children:[/*#__PURE__*/_jsx("img",{className:"genreImage",src:HorrorSVG,alt:""}),/*#__PURE__*/_jsx("p",{className:"genreName",children:"Horror"})]}),/*#__PURE__*/_jsxs("div",{onClick:function onClick(){return _this3.handleGenreClick(5,878);},className:this.state.genreClasses[5],children:[/*#__PURE__*/_jsx("img",{className:"genreImage",src:SciFiSVG,alt:""}),/*#__PURE__*/_jsx("p",{className:"genreName",children:"Science Fiction"})]})]}),/*#__PURE__*/_jsx("div",{onClick:this.userSubmitSurvey,id:"surveyButtonContainer",children:/*#__PURE__*/_jsx("img",{className:"loginButton",src:ArrowSVG,alt:""})})]})]});}}]);return Settings;}(Component);export default Settings;

