import React, {Component} from 'react';

import './Favourites.css';

import MoviesRowFull from './MoviesRowFull';

class Favourites extends Component{
    constructor(props){
        super(props);
        this.state = {
            favourites: JSON.parse((localStorage.getItem('username'))).favourites,
        }
    }
    componentDidMount(){
    }
    
    HandleMovieClick = (movieID) => {
        console.log(movieID);
    }

    render(){
        return(
            <div id='PopularTab'>
                {this.state.favourites.length === 0 ? <p id='nullMoviesTitle'>Hmmmm... Seems There Are No Movies Here Currently</p> : <MoviesRowFull movieClickhandler={this.HandleMovieClick} favourites={this.state.favourites}/>}
            </div>
        )
    }
}

export default Favourites;
